{
  "sql": "WITH user_dates AS (SELECT strftime('%Y-%m-%d %H:%M:00', datetime(created_at, 'unixepoch')) AS time_bucket, COUNT(*) as new_users FROM user GROUP BY strftime('%Y-%m-%d %H:%M:00', datetime(created_at, 'unixepoch'))), buckets AS (SELECT CASE WHEN '$granularity' = 'minute' THEN time_bucket WHEN '$granularity' = 'hour' THEN strftime('%Y-%m-%d %H:00:00', time_bucket) WHEN '$granularity' = 'day' THEN strftime('%Y-%m-%d 00:00:00', time_bucket) WHEN '$granularity' = 'week' THEN strftime('%Y-%m-%d 00:00:00', date(time_bucket, 'weekday 0', '-7 days')) WHEN '$granularity' = 'month' THEN strftime('%Y-%m-01 00:00:00', time_bucket) END AS creation_bucket, SUM(new_users) as bucket_users FROM user_dates GROUP BY creation_bucket) SELECT creation_bucket AS creation_day, bucket_users as new_users, SUM(bucket_users) OVER (ORDER BY creation_bucket) as cumulative_users FROM buckets ORDER BY creation_bucket ASC"
}
